    <Form className="w-full max-w-xs" validationErrors={errors} onSubmit={onSubmit}>
      <Input
        isRequired
        isDisabled={isLoading}
        label="Username"
        labelPlacement="outside"
        name="username"
        placeholder="Enter your username"
      />
      <Button color="primary" isLoading={isLoading} type="submit">
        Submit
      </Button>
    </Form>
  );
}

